package br.eti.tavares.trashpick;import androidx.appcompat.app.AppCompatActivity;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.TextView;import com.google.firebase.auth.FirebaseAuth;import com.google.firebase.auth.FirebaseUser;public class PerfilActivity extends AppCompatActivity {    private FirebaseAuth auth;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_perfil);        auth = FirebaseAuth.getInstance();        FirebaseUser user = auth.getCurrentUser();        final TextView txtNome = findViewById(R.id.txtNome);        txtNome.setText(user.getDisplayName());    }    public void OnClickMaps(View v) {        Intent iMaps = new Intent(getApplicationContext(), MapsActivity.class);        startActivity(iMaps);    }    public void OnClickObjetivos(View v) {        Intent iObjetivos = new Intent(getApplicationContext(), ObjetivosActivity.class);        startActivity(iObjetivos);    }    public void OnClickRanking(View v) {        Intent iRanking = new Intent(getApplicationContext(), RankingActivity.class);        startActivity(iRanking);    }}